                "description": prop["description"],
                "settings": property_settings.get(prop_id, {})
            }
            all_properties.append(prop_data)

    return all_properties


def get_reconcile_properties(strategies, query_filter=None, type_filter=None):
    """Get filtered properties for reconciliation"""
    all_properties = []

    for strategy_name, strategy_class in strategies.items.items():
        # Skip if type filter doesn't match
        if type_filter and strategy_name != type_filter:
            continue

        strategy_instance = strategy_class()
        properties_meta = strategy_instance.get_properties_meta()

        for prop in properties_meta:
            # Apply query filter if provided
            if query_filter:
                query_lower = query_filter.lower()
                if not (query_lower in prop["id"].lower() or 
                       query_lower in prop["name"].lower() or 
                       query_lower in prop.get("description", "").lower()):
                    continue

            all_properties.append({
                "id": prop["id"],
                "name": prop["name"],
                "type": prop["type"],  
                "description": prop["description"]
            })

    return all_properties


