@router.post("/reconcile", response_model=Dict[str, ReconQueryResult])
async def reconcile_entities(request: Request):
    """Handle reconciliation queries"""
    try:
        # Parse request body
        body = await request.body()

        if not body:
            raise HTTPException(status_code=400, detail="Empty request body")

        try:
            data = json.loads(body.decode('utf-8'))
        except json.JSONDecodeError:
            raise HTTPException(status_code=400, detail="Invalid JSON")

        # Extract queries
        queries_data = data.get('queries')
        if not queries_data:
            raise HTTPException(status_code=400, detail="No queries provided")

        # Handle queries as JSON string
        if isinstance(queries_data, str):
            try:
                queries_data = json.loads(queries_data)
            except json.JSONDecodeError:
                raise HTTPException(status_code=400, detail="Invalid queries JSON")

        # Validate queries using Pydantic
        validated_queries = {}
        for key, query_data in queries_data.items():
            try:
                validated_queries[key] = ReconQuery.model_validate(query_data)
            except Exception as e:
                raise HTTPException(status_code=400, detail=f"Invalid query '{key}': {str(e)}")

        # Process queries
        results = await reconcile_queries(queries_data)

        # Validate response format
        validated_results = {}
        for key, result in results.items():
            candidates = []
            for candidate_data in result.get('result', []):
                try:
                    # Ensure type field is properly formatted
                    if 'type' in candidate_data and candidate_data['type']:
                        type_refs = []
                        for type_data in candidate_data['type']:
                            type_refs.append(TypeRef.model_validate(type_data))
                        candidate_data['type'] = type_refs

                    candidate = ReconCandidate.model_validate(candidate_data)
                    candidates.append(candidate)
                except Exception as e:
                    logger.warning(f"Invalid candidate data: {e}")
                    continue

            validated_results[key] = ReconQueryResult(result=candidates)

        return validated_results

    except HTTPException:
        raise
    except Exception as e:
        logger.error(f"Reconciliation error: {e}")
        raise HTTPException(status_code=500, detail="Internal server error")


